%% yyaction(Action, TokenLength, TokenChars, TokenLine) ->
%%        {token,Token} | {end_token, Token} | skip_token | {error,String}.
%% Generated action function.

yyaction(0, _, _, TokenLine) ->
    {token,{'Terminals',TokenLine}};
yyaction(1, _, _, TokenLine) ->
    {token,{'Start',TokenLine}};
yyaction(2, TokenLen, YYtcs, TokenLine) ->
    TokenChars = yypre(YYtcs, TokenLen),
    {token,{nt,TokenLine,list_to_atom(TokenChars)}};
yyaction(3, TokenLen, YYtcs, TokenLine) ->
    TokenChars = yypre(YYtcs, TokenLen),
    {token,{op,TokenLine,list_to_atom(TokenChars)}};
yyaction(4, TokenLen, YYtcs, TokenLine) ->
    TokenChars = yypre(YYtcs, TokenLen),
    {token,{action,TokenLine,stripa(TokenChars)}};
yyaction(5, TokenLen, YYtcs, TokenLine) ->
    TokenChars = yypre(YYtcs, TokenLen),
    {token,{cost,TokenLine,stripc(TokenChars)}};
yyaction(6, TokenLen, YYtcs, TokenLine) ->
    TokenChars = yypre(YYtcs, TokenLen),
    {token,{cost,TokenLine,list_to_integer(TokenChars)}};
yyaction(7, _, _, TokenLine) ->
    {token,{'<-',TokenLine}};
yyaction(8, _, _, TokenLine) ->
    {token,{'(',TokenLine}};
yyaction(9, _, _, TokenLine) ->
    {token,{')',TokenLine}};
yyaction(10, _, _, TokenLine) ->
    {token,{',',TokenLine}};
yyaction(11, _, _, TokenLine) ->
    {token,{'.',TokenLine}};
yyaction(12, _, _, _) ->
    skip_token;
yyaction(13, _, _, _) ->
    skip_token;
yyaction(14, _, _, TokenLine) ->
    {end_token,{'$end',TokenLine}};
yyaction(_, _, _, _) -> error.
